      cardPlacementType
      contact
      displayCTATitle
      emailId
      moduleId
      phoneCode
      phoneNumber
      redirectData
      redirectTo
      status
      type
    }
    hotelId
    id
    isActive
    lang
    offerCardName
    offerCardType
    offerId
    offerName
    offerRecipient
    pk
    popUpImages {
      fileName
      index
    }
    postScratchImages {
      fileName
      index
    }
    preScratchImages {
      fileName
      index
    }
    schedule {
      alwaysActive
      oneTime {
        date
        time
      }
      recurring {
        dates
        endDate
        impression
        recurs
        recursTime
        recursWeek
        startDate
      }
      scheduleType
    }
    sk
  }
}

